CREATE TABLE coverage (id integer, geom geometry);
INSERT INTO coverage VALUES (1, 'POLYGON((0 0, 10 0, 10.1 5, 10 10, 0 10, 0 0))'), (2, 'POLYGON((10 0, 20 0, 20 10, 10 10, 10.05 5, 10 0))'), (3, 'POLYGON((20 0, 30 0, 30 10, 20 10, 20 0))');
SELECT id, ST_AsText(ST_CoverageInvalidLocations(geom) OVER ()) FROM coverage;
id | st_astext ----+-------------------------------- 1 | LINESTRING(10 0,10.1 5,10 10) 2 | LINESTRING(10 10,10.05 5,10 0) 3 |

-- Test entire table for validity
SELECT true = ALL (
    SELECT ST_CoverageInvalidLocations(geom) OVER () IS NULL
    FROM coverage
    );

CREATE TABLE coverage (id integer, geom geometry);
