SELECT id, ST_AsText(ST_CoverageSimplify(geom, 1.0) OVER ()) FROM coverage;
id | st_astext ----+--------------------------------------- 1 | POLYGON((10 0,10 10,0 10,0 0,10 0)) 2 | POLYGON((10 0,20 0,20 10,10 10,10 0)) 3 | POLYGON((20 0,30 0,30 10,20 10,20 0))

CREATE TABLE coverage (id integer, geom geometry);

INSERT INTO coverage VALUES
  (1, 'POLYGON((0 0, 10 0, 10.1 5, 10 10, 0 10, 0 0))'),
  (2, 'POLYGON((10 0, 20 0, 20 10, 10 10, 10.1 5, 10 0))'),
  (3, 'POLYGON((20 0, 30 0, 30 10, 20 10, 20 0))');

SELECT ST_AsText(ST_CoverageUnion(geom))
  FROM coverage;

                        st_astext
----------------------------------------------------------
